#include <stdio.h>
#include <string>
#include "gtest/gtest.h"

using namespace std;

/*
 Implement wildcard pattern matching with support for '?' and '*'.
 
 '?' Matches any single character.
 '*' Matches any sequence of characters (including the empty sequence).
 
 The matching should cover the entire input string (not partial).
 
 The function prototype should be:
 bool isMatch(const char *s, const char *p)
 
 Some examples:
 isMatch("aa","a") → false
 isMatch("aa","aa") → true
 isMatch("aaa","aa") → false
 isMatch("aa", "*") → true
 isMatch("aa", "a*") → true
 isMatch("ab", "?*") → true
 isMatch("aab", "c*a*b") → false
*/

namespace WildCardMatching {
    class Solution {
    public:
        bool isMatch(string s, string p) {
			_slen = (int)s.length();
			_plen = (int)p.length();
            for(int i = 0; i < _slen + 2; ++ i){
                vector<char> temp(_plen + 2, -1);
                _status.push_back(temp);
            }
			return isMatch(s, 0, p, 0);
        }
	private:
		bool isMatch(string& s, int si, string& p, int pi){
            if(_status[si][pi] >= 0)
                return _status[si][pi];
            bool result = false;
            if(si == _slen && pi == _plen)
                result = true;
            else if(si == _slen && p[pi] != '*')
                result = false;
            else if(pi == _plen)
                result = false;
            else if(p[pi] == '*'){
                if(pi + 1 < _plen && p[pi + 1] == '*')
                    result = isMatch(s, si, p, pi + 1);
                for(int i = 0; i <= _slen - si; ++ i){
                    if(isMatch(s, si + i, p, pi + 1)){
                        result = true;
                        break;
                    }
                }
            }
            else if(p[pi] == '?' || s[si] == p[pi])
                result = isMatch(s, si + 1, p, pi + 1);
            _status[si][pi] = result;
            return result;
		}
	private:
		int _slen;
		int _plen;
        vector<vector<char>> _status;
    };
    
    class wdmhelper{
    public:
        void test(string s, string p, bool expected){
            Solution sln;
            bool actual = sln.isMatch(s, p);
			ASSERT_EQ(expected, actual);
        }
        
    };
	TEST(WildcardMatching, isMatch) {
		wdmhelper h;
		h.test("aa", "a", false);
		h.test("aa", "aa", true);
		h.test("aaa", "aa", false);
		h.test("aa", "*", true);
		h.test("aa", "a*", true);
		h.test("ab", "?*", true);
		h.test("aab", "c*a*b", false);
		h.test("aa", "aa*", true);
        h.test("ab", "*a", false);
    h.test("", "**",
           false);
    }
}
